"use strict";
// Basic Data Types for TypeScript
/*
Primitive Types
----------------------------------------------------------------
string
number
boolean
undefined
null
*/
let firstName = 'mejba'; //string
let roll = 13; //number
let isAdmin = true; //boolean
let x = undefined; //undefined
let y = null; //null
let d; //any data type, should not be used
let n;
n = 123;
/*
 non primitive types
 ----------------------------------------------------------------
 Array
 tuple : array --> order--> types of values

*/
//array
let friends = ['mejba', 'sajib'];
let eligibleRolls = [1, 2, 3];
eligibleRolls.push(4);
//tuple
let coordinates = [1, 5];
let ageName = [50, 'Mr X'];
